sys_variable_value.value:
//Enter Lab 9.3 script here.
(function execute(inputs, outputs){
    var startDate = new GlideDateTime(inputs.created_on);
    var endDate = new GlideDateTime(inputs.resolved_at);
    var calcDiff = new GlideDateTime.subtract(startDate, endDate).getRoundedDayPart();

    var report = "After Action Report\n" + "Subject: After Action Report, " + inputs.assigned_to + " - " + inputs.assignment_group + "\n" + inputs.created_on + " - " + inputs.resolved_at + "\n\n" + inputs.number + " with " + inputs.short_description + " was entered into ServiceNow by " + inputs.created_by + " for " + inputs.caller_id + ". " + "The Configuration Item affected was " + inputs.cmdb_ci + ". " + "Resolved by: " + inputs.resolved_by + " with a Resolution code of " + inputs.close_code + ". " + "Resolution notes are as follows: " + inputs.close_notes + "\n\n" + "Total time: " + calcDiff + " Days"; 

    outputs.payload = report;

})(inputs, outputs);

